Component({
  props: {
    mask: true,
    maskClose: true,
    animation: true,
    show: true,
    disableScroll: false,
    className: '',
    popupClass: '',
    isContentScrollView: true,
    distanceFromTop: 50,
    zIndex: 2,
    title: 'Bottom sheet',
    buttonTitle: 'ok',
    buttonShape: 'rounded',
    buttonCls: '',
    onClose: undefined,
    onClick: undefined
  },
  didMount: function didMount() {
    this._updateDataSet();
  },
  didUpdate: function didUpdate() {
    this._updateDataSet();
  },
  methods: {
    _updateDataSet: function _updateDataSet() {
      this.dataset = {};

      for (var key in this.props) {
        if (/data-/gi.test(key)) {
          this.dataset[key.replace(/data-/gi, '')] = this.props[key];
        }
      }
    },
    onClose: function onClose() {
      this.setData({
        show: false
      });
      var onClose = this.props.onClose;

      if (onClose) {
        onClose({
          target: {
            dataset: this.dataset
          }
        });
      }
    },
    onClick: function onClick() {
      var onClick = this.props.onClick;

      if (onClick) {
        onClick({
          target: {
            dataset: this.dataset
          }
        });
      }
    }
  }
});